export const theme = {
  colors: {
    primary: '#2563eb',    // Modern blue
    secondary: '#3b82f6',  // Lighter blue
    accent: '#1d4ed8',     // Darker blue
    background: '#ffffff',
    text: '#1f2937',
    lightText: '#6b7280',
    lightGray: '#f3f4f6',
    darkGray: '#374151'
  },
  shadows: {
    small: '0 1px 3px rgba(0,0,0,0.12)',
    medium: '0 4px 6px rgba(0,0,0,0.1)',
    large: '0 10px 15px rgba(0,0,0,0.1)'
  },
  transitions: {
    default: 'all 0.3s ease',
    slow: 'all 0.5s ease'
  }
};
