export const theme = {
  colors: {
    primary: '#2563eb',    // Main blue
    secondary: '#3b82f6',  // Lighter blue for hover states
    accent: '#1d4ed8',     // Darker blue for active states
    background: '#ffffff', // White
    text: '#000000',      // Black
    lightText: '#4b5563', // Dark gray for secondary text
    lightGray: '#f8fafc', // Very light gray/blue for backgrounds
    darkGray: '#1f2937'   // Dark gray for contrast
  },
  shadows: {
    small: '0 1px 3px rgba(37, 99, 235, 0.12)',     // Blue tinted shadows
    medium: '0 4px 6px rgba(37, 99, 235, 0.1)',
    large: '0 10px 15px rgba(37, 99, 235, 0.1)'
  },
  transitions: {
    default: 'all 0.3s ease',
    slow: 'all 0.5s ease'
  }
};
