import React from 'react';
import styled from 'styled-components';

const AboutContainer = styled.div`
  padding: 4rem 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const Hero = styled.div`
  text-align: center;
  margin-bottom: 4rem;
`;

const TeamGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 2rem;
  margin-top: 3rem;
`;

const TeamMember = styled.div`
  text-align: center;
`;

const TeamMemberImage = styled.img`
  width: 200px;
  height: 200px;
  border-radius: 50%;
  object-fit: cover;
  margin-bottom: 1rem;
`;

const Stats = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 2rem;
  margin: 4rem 0;
  text-align: center;
`;

const About = () => {
  return (
    <AboutContainer>
      <Hero>
        <h1>About Us</h1>
        <p>We're a team of passionate tech enthusiasts dedicated to transforming businesses through innovative IT solutions.</p>
      </Hero>

      <Stats>
        <div>
          <h2>200+</h2>
          <p>Projects Completed</p>
        </div>
        <div>
          <h2>50+</h2>
          <p>Team Members</p>
        </div>
        <div>
          <h2>15+</h2>
          <p>Years Experience</p>
        </div>
        <div>
          <h2>100%</h2>
          <p>Client Satisfaction</p>
        </div>
      </Stats>

      <h2>Our Team</h2>
      <TeamGrid>
        <TeamMember>
          <TeamMemberImage src="/team/ceo.jpg" alt="CEO" />
          <h3>John Doe</h3>
          <p>CEO & Founder</p>
        </TeamMember>
        <TeamMember>
          <TeamMemberImage src="/team/cto.jpg" alt="CTO" />
          <h3>Jane Smith</h3>
          <p>CTO</p>
        </TeamMember>
        <TeamMember>
          <TeamMemberImage src="/team/lead-designer.jpg" alt="Lead Designer" />
          <h3>Mike Johnson</h3>
          <p>Lead Designer</p>
        </TeamMember>
        <TeamMember>
          <TeamMemberImage src="/team/lead-developer.jpg" alt="Lead Developer" />
          <h3>Sarah Williams</h3>
          <p>Lead Developer</p>
        </TeamMember>
      </TeamGrid>
    </AboutContainer>
  );
};

export default About;
