import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const BlogContainer = styled(motion.div)`
  padding: 6rem 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const BlogGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2.5rem;
  margin-top: 3rem;
`;

const BlogCard = styled(motion.div)`
  border-radius: 16px;
  overflow: hidden;
  box-shadow: ${props => props.theme.shadows.medium};
  transition: ${props => props.theme.transitions.default};
  background: white;

  &:hover {
    transform: translateY(-5px);
    box-shadow: ${props => props.theme.shadows.large};
  }
`;

const BlogImage = styled.img`
  width: 100%;
  height: 240px;
  object-fit: cover;
  transition: ${props => props.theme.transitions.default};

  ${BlogCard}:hover & {
    transform: scale(1.05);
  }
`;

const BlogContent = styled.div`
  padding: 2rem;

  h2 {
    color: ${props => props.theme.colors.text};
    font-size: 1.5rem;
    margin-bottom: 1rem;
    line-height: 1.4;
  }

  p {
    color: ${props => props.theme.colors.lightText};
    margin-bottom: 1.5rem;
    line-height: 1.6;
  }
`;

const Tag = styled.span`
  background: ${props => props.theme.colors.lightGray};
  color: ${props => props.theme.colors.primary};
  padding: 0.5rem 1rem;
  border-radius: 50px;
  font-size: 0.875rem;
  font-weight: 500;
  margin-right: 0.5rem;
  transition: ${props => props.theme.transitions.default};

  &:hover {
    background: ${props => props.theme.colors.primary};
    color: white;
  }
`;

const Blog = () => {
  const blogPosts = [
    {
      title: "Getting Started with React 18",
      description: "Learn about the latest features in React 18 and how to implement them.",
      image: "/blog/react.jpg",
      tags: ["React", "JavaScript"],
      date: "May 15, 2024"
    },
    {
      title: "Cloud Computing Trends 2024",
      description: "Explore the latest trends in cloud computing and their impact on businesses.",
      image: "/blog/cloud.jpg",
      tags: ["Cloud", "AWS"],
      date: "May 12, 2024"
    },
    {
      title: "UI/UX Design Principles",
      description: "Essential principles for creating user-friendly interfaces.",
      image: "/blog/uiux.jpg",
      tags: ["UI/UX", "Design"],
      date: "May 10, 2024"
    },
    {
      title: "TypeScript Best Practices",
      description: "Learn how to write better TypeScript code with these best practices.",
      image: "/blog/typescript.jpg",
      tags: ["TypeScript", "JavaScript"],
      date: "May 8, 2024"
    }
  ];

  return (
    <BlogContainer
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <h1>Tech Blog</h1>
      <BlogGrid>
        {blogPosts.map((post, index) => (
          <BlogCard
            key={index}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: index * 0.1 }}
          >
            <BlogImage src={post.image} alt={post.title} />
            <BlogContent>
              <h2>{post.title}</h2>
              <p>{post.description}</p>
              <div style={{ margin: '1rem 0' }}>
                {post.tags.map((tag, i) => (
                  <Tag key={i}>{tag}</Tag>
                ))}
              </div>
              <small>{post.date}</small>
            </BlogContent>
          </BlogCard>
        ))}
      </BlogGrid>
    </BlogContainer>
  );
};

export default Blog;
